                            <div className="space-y-1">
                              {Object.entries(cost).map(([key, value]: [string, any]) => (
                                <div key={key} className="text-sm">
                                  <span className="font-medium">{key}:</span> {
                                    typeof value === 'object' && value !== null ? JSON.stringify(value) : String(value)
                                  }
                                </div>
                              ))}
                            </div>
