                </div>
                <p className="text-neutral-dark">{user?.username || 'N/A'}</p>
              </div>

              <div className="space-y-2">
                <div className="flex items-center space-x-2 text-sm font-medium text-gray-600">
                  <Mail className="w-4 h-4" />
